mapper.select:
-- CodeDao.selectCodeCnt
SELECT  COUNT(*) CNT
FROM    TDACM00060 A
WHERE   A.CD_ID_HNM = #{CD_ID_HNM}
AND     A.CD        = #{CD}

mapper.insert:
	-- CodeDao.insertCode
	INSERT INTO TDACM00060
	(
         CD_ID_NM     
        ,CD_ID_HNM    
        ,CD_ID_GRP_NM 
        ,CD           
        ,CD_NM        
        ,CD_HNM       
        ,CD_DESC      
        ,CD_TY_CD     
        ,CD_STS_CD    
        ,REG_DTM      
        ,REG_USR_ID   
        ,UPD_DTM      
        ,UPD_USR_ID   
	) VALUES (
         #{CD_ID_NM}
        ,#{CD_ID_HNM}
        ,#{CD_ID_GRP_NM}
        ,#{CD}
        ,#{CD_NM}
        ,#{CD_HNM}
        ,#{CD_DESC}
        ,#{CD_TY_CD}
        ,#{CD_STS_CD}
		,curdate()
		,#{REG_USR_ID}
		,curdate()
		,#{UPD_USR_ID}
	)

mapper.update:
	-- CodeDao.updateCode
	UPDATE TDACM00060
	SET  CD_ID_NM      = #{CD_ID_NM}
		,CD_ID_HNM     = #{CD_ID_HNM}
		,CD_ID_GRP_NM  = #{CD_ID_GRP_NM}
        ,CD            = #{CD}
        ,CD_NM         = #{CD_NM}
        ,CD_HNM        = #{CD_HNM}
        ,CD_DESC       = #{CD_DESC}
        ,CD_TY_CD      = #{CD_TY_CD}
        ,CD_STS_CD     = #{CD_STS_CD}
		,UPD_DTM       = curdate()
		,UPD_USR_ID    = #{UPD_USR_ID}
	WHERE  CD_ID_NM = #{CD_ID_NM}
	AND    CD       = #{CD}
